function plotFigure2()  load competition_evening/trdata.mat.gz  load competition_evening/trlabels.mat.gz  load competition_evening/tedata.mat.gz  load competition_evening/telabels.mat.gz    V = lda(X, xl);  errorsLDA = [];  i = [1:5:99];    for j = 1:5:99    Y1 = V(:,1:j)'*Y;    X1 = V(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errorsLDA = [errorsLDA; err];  end  figure   axis([1 99 90 100])  xlabel(['Dimensionalidad espacio reducido'],'FontSize',16)  ylabel(['Error (%)'],'FontSize',16)  box on  hold on  plot(i, errorsLDA,'-+r')  legend({'LDA'})  hold on  
endfunction
