function competicionPCA()  load competition_evening/trdata.mat.gz  load competition_evening/trlabels.mat.gz  load competition_evening/tedata.mat.gz  load competition_evening/telabels.mat.gz    [m, W] = pca(X);  errors = [];  i = [45:1:55];  erro1 = knn(X, xl, Y, yl, 1);  erro1 = erro1 + i*0;  Y = Y - m;  X = X - m;  for j = 45:1:55    Y1 = W(:,1:j)'*Y;    X1 = W(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errors = [errors; err];  end  figure   axis([40 60 30 50])  xlabel(['Dimensionalidad espacio PCA'],'FontSize',16)  ylabel(['Error (%)'],'FontSize',16)  box on  hold on  plot(i, erro1,'-b',i, errors,'-xg')  legend({'Original', 'PCA'})
endfunction
