function plotFigure1()  load trdata.mat.gz;  load trlabels.mat.gz;  load tedata.mat.gz;  load telabels.mat.gz;    [m, W] = pca(X);  errors = [];  i = [10:10:100];  erro1 = knn(X, xl, Y, yl, 1);  erro1 = erro1 + i*0;  Y = Y - m;  X = X - m;  for j = 10:10:100    Y1 = W(:,1:j)'*Y;    X1 = W(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errors = [errors; err];  end  figure   axis([10 100 4 11])  xlabel(['Dimensionalidad espacio PCA'],'FontSize',16)  ylabel(['Error (%)'],'FontSize',16)  box on  hold on  plot(i, erro1,'-b',i, errors,'-xg')  legend({'Original', 'PCA'})
endfunction
