function plotFigure2()  load trdata.mat.gz;  load trlabels.mat.gz;  load tedata.mat.gz;  load telabels.mat.gz;    V = lda(X, xl);  errorsLDA = [];  i = [1:1:9];    for j = 1:1:9    Y1 = V(:,1:j)'*Y;    X1 = V(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errorsLDA = [errorsLDA; err];  end  [m, W] = pca(X);  Y = Y - m;  X = X - m;  errorsPCA = [];    for j = 1:1:9    Y1 = W(:,1:j)'*Y;    X1 = W(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errorsPCA = [errorsPCA; err];  end    figure   axis([1 9 10 70])  xlabel(['Dimensionalidad espacio reducido'],'FontSize',16)  ylabel(['Error (%)'],'FontSize',16)  box on  hold on  plot(i, errorsLDA,'-+r', i, errorsPCA, '-xg')  legend({'LDA', 'PCA'})  hold on  
endfunction
