function plotFigure2()  load trdata.mat.gz;  load trlabels.mat.gz;  load tedata.mat.gz;  load telabels.mat.gz;    V = lda(X, xl);  errorsLDA = [];  i = [1:1:9];    for j = 1:1:9    Y1 = V(:,1:j)'*Y;    X1 = V(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errorsLDA = [errorsLDA; err];  end  [m, W] = pca(X);  Y = Y - m;  X = X - m;  errorsPCA = [];    for j = 1:1:9    Y1 = W(:,1:j)'*Y;    X1 = W(:,1:j)'*X;    err = knn(X1, xl, Y1, yl, 1);    errorsPCA = [errorsPCA; err];  end    figure   hold on  plot(i, errorsLDA)  plot(i, errorsPCA)
endfunction
