function plotFigure3()  load trdata.mat.gz;  load trlabels.mat.gz;  load tedata.mat.gz;  load telabels.mat.gz;    [m,W] = pca(X);  i = [1:1:9];  X = X - m;  Y = Y - m;  errors = zeros(10,9);    for j = 10:10:100    x1 = W(:,1:j)'*X;    y1 = W(:,1:j)'*Y;        V = lda(x1, xl);     for k = 1:1:9      x2 = V(:,1:k)'*x1;      y2 = V(:,1:k)'*y1;      err = knn(x2, xl, y2, yl, 1);      errors(j/10,k) = err;    end  end    figure  hold on  for j = 1:1:10    plot(i, errors(j,:))    endendfunction